package ar.edu.ies6.informatica.punto01;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Declarar y almacenar los datos solicitados
        String nombrePais = "España"; 
        int edadPersona = 25; 
        double alturaEdificio = 150.5; 
        double precioProducto = 23.75; 
        String numeroTelefono = "123-456-7890"; 
        
        // Cálculo del coseno de 0.5
        double coseno = Math.cos(0.5);
        
        // Declaramos 5 variables enteras y asignamos valores numéricos
        int valor1 = 8;
        int valor2 = 5;
        int valor3 = 1;
        int valor4 = 20;
        int valor5 = 9;
        
        // Calcular el promedio de las 5 variables
        double promedio = (valor1 + valor2 + valor3 + valor4 + valor5) / 5.0;
        
        // Mostrar los resultados por consola
        System.out.println("Nombre del país: " + nombrePais);
        System.out.println("Edad de la persona: " + edadPersona);
        System.out.println("Altura del edificio: " + alturaEdificio + " metros");
        System.out.println("Precio del producto: $" + precioProducto);
        System.out.println("Número de teléfono: " + numeroTelefono);
        System.out.println("Cálculo del coseno de 0.5: " + coseno);
        System.out.println("El promedio es: " + promedio);
    





	}

}
